sys_variable_value.value:
(function execute(inputs, outputs) {
  var recordGr = inputs.docid;
  var activity = inputs.activityid.toString();

  var processContext = new GlideRecord('sys_pd_context');
  processContext.addQuery('input_record',recordGr);
  processContext.query();

  if(processContext.next()){
    var activityContext = new GlideRecord('sys_pd_activity_context');
    activityContext.addQuery('activity',activity);
    activityContext.addQuery('context',processContext.getUniqueValue());
    activityContext.query();
    if(activityContext.next()){
      var flowData = new GlideRecord('sys_flow_data');
      flowData.get(activityContext.associated_record.toString());
      var fdrecord = flowData.getUniqueValue();
      outputs.fdrecordid = fdrecord;
    }
  }
})(inputs, outputs);

