sys_variable_value.value:
(function execute(inputs, outputs) {
  var docId = inputs.invDocumentId;
  var varValue = inputs.varValue;
  var varName = inputs.varName;
  var invGr = new GlideRecord('u_invoice');
  invGr.get(docId);
  invGr.variables[varName] = varValue;
  invGr.update()
})(inputs, outputs);

