sys_variable_value.value:
(function execute(inputs, outputs) {

  var documentId = inputs.RecordSysId.toString();
  var cancelReason = inputs.CancelReason.toString();
  var flowName = inputs.FlowName.toString();

  var flow = new GlideRecord("sys_flow_context");
  flow.addQuery('name', flowName); // flow name
  flow.addQuery('source_record', documentId);
  flow.addEncodedQuery('stateINWAITING,IN_PROGRESS,QUEUED');
  flow.query();
  while(flow.next()){

    var gpa = new sn_ph.GlideProcessAutomation(flow.getValue('sys_id'));

    if (!gpa.isTerminated()) {
      //cancel the flow and provide the reason
      gpa.cancel(cancelReason);
    }
  }
})(inputs, outputs);

