sys_variable_value.value:
(function execute(inputs, outputs) {
  var current = inputs.current;
  var catItem = inputs.is_producer ? new GlideRecord('sc_cat_item_producer') : new GlideRecord('sc_cat_item');
  catItem.get(inputs.catalog_item.sys_id);
  if(catItem.table_name == 'u_invoice'){
    var docId = current.sys_id;
    current = new GlideRecord('u_invoice');
    current.get(docId);
  }
  startFlowDesignerFlow(current, catItem);

  function startFlowDesignerFlow(current, catItem) {
    var flow = catItem.flow_designer_flow;
    if (catItem.table_name == 'u_invoice'){
      flow = new GlideRecord('sys_hub_flow');
      flow.get('daeb8db91bea0dd08c5143f3cc4bcbe2');
    }
    var flowName = flow.sys_scope.scope + "." + flow.internal_name;

    sn_flow_trigger.FlowTriggerAPI.fireCatalogTrigger(flowName, current);
  }  

})(inputs, outputs);

