var approverComments = '';
var approverString = '';
var number = fd_data.flow_var.invoice_number;
var approvalAmount = fd_data.subflow_inputs.invoice_amount.toString();
var approverMaxAmount = fd_data.subflow_inputs.approver_max_approval_amount.toString();

//Build comment
