approverString += approver + ': ' + state;
approverComments += commentString ? commentString : '';

//If original comments aren't blank then add space between comments and built comments
var comments = originalComments == '' ? '' : originalComments + '<br><br>'; 
comments += (approverString ? '<b>Approver:</b><br>' + approverString : '') +
		(approverComments ? '<br><b>Comments:</b><br>' + approverComments : '') +
		(approvalAmount ? '<br><b>Approval Amount:</b><br>' + approvalAmount : '') +
		(approverMaxAmount ? '<br><b>Approver Max Approval Amount:</b><br>' + approverMaxAmount : '');

return comments;"}}

