sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var tableName = current.table_name.toString() == 'u_invoice' ? 'u_invoice_task' : 'u_invoice';

	var invTaskGr = new GlideRecord('u_invoice_task');
	//If it's an invoice task that was updated, we're only getting one invoice record
	if(tableName == 'u_invoice'){
		invTaskGr.addQuery('sys_id',current.table_sys_id.toString());
	}
	else{
		var invGr = new GlideRecord('u_invoice');
		invGr.get(current.table_sys_id);
		invTaskGr.addQuery('parent',invGr.sys_id.toString());
	}
	invTaskGr.query();
	while(invTaskGr.next()){
		//TODO: Change query
		var qa = new GlideRecord('question_answer');
		if(tableName == 'u_invoice'){
			qa.addQuery('table_sys_id',invTaskGr.parent.sys_id.toString());
		}
		else{
			qa.addQuery('table_sys_id',invTaskGr.sys_id.toString());
		}
		qa.addQuery('table_name',tableName);
		qa.addQuery('question',current.question);
		qa.addQuery('value','!=',current.value.toString()).addOrCondition('value','=','');
		qa.query();
		while(qa.next()) {
			qa.value = current.value;
			qa.update();
		}
	}
})(current, previous);
